/**
 * Gift wrapping adminhtml sales order create items
 *
 * @category   Enterprise
 * @package    Enterprise_GiftWrapping
 * @author     Magento Core Team <core@magentocommerce.com>
 */
class Enterprise_GiftWrapping_Block_Adminhtml_Sales_Order_Create_Link extends Mage_Adminhtml_Block_Template
{
    /**
     * Get order item from parent block
     *
     * @return Mage_Sales_Model_Order_Item
     */
    public function getItem()
    {
        return $this->getParentBlock()->getItem();
    }

    /**
     * Get gift wrapping design
     *
     * @return string
     */
    public function getDesign()
    {
        if ($this->getItem()->getGwId()) {
            $wrappingModel = Mage::getModel('enterprise_giftwrapping/wrapping')->load($this->getItem()->getGwId());
            if ($wrappingModel->getId()) {
                return $this->htmlEscape($wrappingModel->getDesign());
            }
        }
        return '';
    }

    /**
     * Check ability to display gift wrapping for order items
     *
     * @return bool
     */
    public function canDisplayGiftWrappingForItem()
    {
        $allowed = $this->getItem()->getProduct()->getGiftWrappingAvailable();
        $storeId = $this->getItem()->getStoreId();
        return Mage::helper('enterprise_giftwrapping')->isGiftWrappingAvailableForProduct($allowed, $storeId);
    }
}
